!(function(a) {
  "use strict";
  a('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function() {
    if (
      location.pathname.replace(/^\//, "") ==
        this.pathname.replace(/^\//, "") &&
      location.hostname == this.hostname
    ) {
      var e = a(this.hash);
      if ((e = e.length ? e : a("[name=" + this.hash.slice(1) + "]")).length)
        return (
          a("html, body").animate(
            { scrollTop: e.offset().top - 57 },
            1e3,
            "easeInOutExpo"
          ),
          !1
        );
    }
  }),
    a(".js-scroll-trigger").click(function() {
      a(".navbar-collapse").collapse("hide");
    }),
    a("body").scrollspy({ target: "#mainNav", offset: 57 });
  var e = function() {
    a("#mainNav").offset().top > 100
      ? a("#mainNav").addClass("navbar-shrink")
      : a("#mainNav").removeClass("navbar-shrink");
  };
  e(),
    a(window).scroll(e),
    (window.sr = ScrollReveal()),
    sr.reveal(".sr-icons", { duration: 600, scale: 0.3, distance: "0px" }, 200),
    sr.reveal(".sr-button", { duration: 1e3, delay: 200 }),
    sr.reveal(
      ".sr-contact",
      { duration: 600, scale: 0.3, distance: "0px" },
      300
    ),
    a(".popup-gallery").magnificPopup({
      delegate: "a",
      type: "image",
      tLoading: "Loading image #%curr%...",
      mainClass: "mfp-img-mobile",
      gallery: { enabled: !0, navigateByImgClick: !0, preload: [0, 1] },
      image: {
        tError: '<a href="%url%">The image #%curr%</a> could not be loaded.'
      }
    });
})(jQuery);
document.addEventListener("DOMContentLoaded", function(event) {
  // array with texts to type in typewriter
  var dataText = [
    "Husband",
    "Buckeye",
    "Pit Master",
    "Dog Dad",
    "Ice Cream Snob",
    "FullStack WebDeveloper"
  ];

  // type one text in the typwriter
  // keeps calling itself until the text is finished
  function typeWriter(text, i, fnCallback) {
    // chekc if text isn't finished yet
    if (i < text.length) {
      // add next character to h1
      document.querySelector("h2").innerHTML =
        text.substring(0, i + 1) + '<span aria-hidden="true"></span>';

      // wait for a while and call this function again for next character
      setTimeout(function() {
        typeWriter(text, i + 1, fnCallback);
      }, 100);
    } else if (typeof fnCallback == "function") {
      // text finished, call callback if there is a callback function
      // call callback after timeout
      setTimeout(fnCallback, 800);
    }
  }
  // start a typewriter animation for a text in the dataText array
  function StartTextAnimation(i) {
    if (typeof dataText[i] == "undefined") {
      setTimeout(function() {
        StartTextAnimation(0);
      }, 4000);
    }
    // check if dataText[i] exists
    if (i < dataText[i].length) {
      // text exists! start typewriter animation
      typeWriter(dataText[i], 0, function() {
        // after callback (and whole text has been animated), start next text
        StartTextAnimation(i + 1);
      });
    }
  }
  // start the text animation
  StartTextAnimation(0);
});
